mapper.select:
select count(*) from t_proprietor_market tm left join t_proprietor_market_category tpmc on tm.category_id = tpmc.category_id left join t_proprietor_market_label tpml on tm.label_id = tpml.label_id left join t_user tu on tm.uid = tu.uid left join t_community tc on tc.id = tm.community_id where tm.shield= 0 and tm.remove = 0
and tm.category_id=#{query.categoryId}
and tm.community_id=#{query.communityId}
AND (tu.real_name LIKE CONCAT('%',#{query.realName,jdbcType=VARCHAR},'%') OR tm.phone LIKE CONCAT('%',#{query.realName,jdbcType=VARCHAR},'%'))
AND state= #{query.state,jdbcType=INTEGER}

select tm.id,tm.community_id as communityId,tc.name as communityName, tm.uid as uid, tm.goods_name as goodsName, tm.price, tm.goods_explain as goodsExplain, tm.negotiable, tm.state, tm.category_id as categoryId,tm.label_id as labelId, tm.remove, tm.click as click, tm.phone as phone,tm.images as images,tm.shield as shield, tm.deleted as deleted, tm.create_time as createTime,tm.update_time as updateTime, tpml.label as labelName,tpmc.category as categoryName,tu.real_name as realName from t_proprietor_market tm left join t_proprietor_market_category tpmc on tm.category_id = tpmc.category_id left join t_proprietor_market_label tpml on tm.label_id = tpml.label_id left join t_user tu on tm.uid = tu.uid left join t_community tc on tc.id = tm.community_id where tm.shield= 0 and tm.remove = 0
and tm.category_id=#{query.categoryId}
and tm.community_id=#{query.communityId}
AND (tu.real_name LIKE CONCAT('%',#{query.realName,jdbcType=VARCHAR},'%') OR tm.phone LIKE CONCAT('%',#{query.realName,jdbcType=VARCHAR},'%'))
AND state= #{query.state,jdbcType=INTEGER}
limit #{page},#{size}
