mapper.select:
select * from t_asset_lease_record


        asset_type = #{entity.assetType}


        and home_owner_uid = #{entity.homeOwnerUid}


        tenant_uid = #{entity.tenantUid}

select * from t_asset_lease_record

        id = #{entity.id}

            and home_owner_uid = #{entity.homeOwnerUid}


            tenant_uid = #{entity.tenantUid}
