mapper.insert:
insert into t_house_lease (id,uid,house_title,house_city_id,house_address,house_advantage_id,house_price,house_square_meter,house_leasedeposit_id
,house_type_code,house_floor,house_direction_id,appellation
,house_introduce,house_image_id,house_leasetype_id,house_leasemode_id,house_contact,house_lon,house_lat,house_id,house_community_id,create_time,

    common_facilities_id,


    roommate_expect_id,

room_facilities_id,bedroom_type,decoration_type_id,roommate_sex,house_area_id
)
values
(#{id},#{uid},#{houseTitle},#{houseCityId},#{houseAddress},#{houseAdvantageId},#{housePrice},#{houseSquareMeter},#{houseLeasedepositId}
,#{houseTypeCode},#{houseFloor},#{houseDirectionId},#{appellation}
,#{houseIntroduce},#{houseImageId},#{houseLeasetypeId},#{houseLeasemodeId},#{houseContact},#{houseLon},#{houseLat},#{houseId},#{houseCommunityId},now(),

    #{commonFacilitiesId},


    #{roommateExpectId},

#{roomFacilitiesId},#{bedroomType},#{decorationTypeId},#{roommateSex},#{houseAreaId}
)

mapper.select:
select
id,bedroom_type,house_direction_id,decoration_type_id,house_title,house_address,house_advantage_id,house_price,house_unit,house_square_meter,house_image_id,house_leasemode_id,house_type_code,house_lon,house_lat,house_furniture_id
from t_house_lease



        house_title like concat('%',#{query.searchText},'%') or house_address like concat('%',#{query.searchText},'%')



        and house_area_id = #{query.houseAreaId}



        and house_price > #{query.housePriceMin} and house_price < #{query.housePriceMax}



        and house_square_meter > #{query.houseSquareMeterMin} and house_square_meter <
        #{query.houseSquareMeterMax}



        and house_type_code = #{query.houseTypeCode}



        and house_leasetype_id = #{query.houseLeasetypeId}



        and house_leasemode_id = #{query.houseLeasemodeId}



        and house_source_id = #{query.houseSourceId}



        and #{query.houseAdvantageId} | house_advantage_id = house_advantage_id

    and deleted  = 0

order by create_time desc
limit #{page},#{size}

select house_const_name,house_const_code from t_house_const where
house_const_type = #{type}
and house_const_code in

    #{code}

select house_const_name from t_house_const where
house_const_type = #{type}
and house_const_code in

    #{code}

mapper.update:
update t_house_lease




            ,house_id = #{houseId}



            ,roommate_expect_id = #{roommateExpectId}



            ,roommate_sex = #{roommateSex}



            ,common_facilities_id = #{commonFacilitiesId}



            ,bedroom_type = #{bedroomType}



            ,room_facilities_id = #{roomFacilitiesId}



            ,appellation = #{appellation}



            ,house_community_id = #{houseCommunityId}



            ,house_area_id = #{houseAreaId}



            ,house_title = #{houseTitle}



            ,house_address = #{houseAddress}



            ,house_advantage_id = #{houseAdvantageId}



            ,house_price = #{housePrice}



            ,house_lon = #{houseLon}



            ,house_lat = #{houseLat}



            ,house_furniture_id = #{houseFurnitureId}



            ,house_contact = #{houseContact}



            ,house_image_id = #{houseImageId}



            ,house_square_meter = #{houseSquareMeter}



            ,house_leasedeposit_id = #{houseLeasedepositId}



            ,house_type_code = #{houseTypeCode}



            ,house_floor = #{houseFloor}



            ,house_direction_id = #{houseDirectionId}



            ,house_introduce = #{houseIntroduce}



            ,house_leasetype_id = #{houseLeasetypeId}



            ,house_leasemode_id = #{houseLeasemodeId}



            ,decoration_type_id = #{decorationTypeId}



            ,lease_require_id = #{leaseRequireId}

        ,update_time = now()



    id = #{id} and uid = #{uid} and deleted = 0
