mapper.update:
update t_admin_user u

	,u.number = #{entity.number}
	,u.real_name = #{entity.realName}
	,u.mobile = #{entity.mobile}
	,u.id_card = #{entity.idCard}
	,u.job = #{entity.job}
	,u.org_id = #{entity.orgId}
	,u.status = #{entity.status}
	,update_time = now(),update_by = #{entity.updateBy}

where id = #{entity.id} and community_id = #{entity.communityId}
