sql.where:
and c.status=#{query.status}
and c.community_id=#{query.communityId}

and c.type=#{query.type}


and c.complain_time >= #{query.complainTimeStart}


and c.complain_time <= #{query.complainTimeOut}


and c.feedback_time >= #{query.feedbackTimeStart}


and c.feedback_time <= #{query.feedbackTimeOut}


and (u.real_name like concat("%",#{query.keyWord},"%") or c.serial_number like concat("%",#{query.keyWord},"%") or u.mobile like concat("%",#{query.keyWord},"%"))
