mapper.select:
SELECT id, real_name, house_id, mobile, id_card, wechat, qq, email FROM t_proprietor
real_name like concat('%',#{query.searchText},'%') or id_card like concat('%', #{query.searchText}, '%') or mobile like concat('%', #{query.searchText}, '%')
LIMIT #{page},#{size}

mapper.update:
update t_user set


        ,mobile = #{mobile}


        ,real_name = #{realName}


        ,id_card = #{idCard}


        ,wechat = #{wechat}


        ,qq = #{qq}


        ,email = #{email}


        ,identification_type = #{identificationType}


where id = #{id} and deleted = 0
