mapper.select:
SELECT DISTINCT tu.uid, tu.real_name as realName, tu.mobile, tu.id, tu.face_url as faceUrl, tu.face_enable_status as faceEnableStatus, CASE tu.face_enable_status WHEN 1 THEN '启用' WHEN 2 THEN '禁用' END AS faceEnableStatusStr, tu.create_time as createTime, CASE ( (SELECT count( tfsr.id ) FROM t_user_face_sync_record tfsr WHERE tfsr.uid = tu.uid AND tfsr.face_url = tu.face_url AND tfsr.community_id = #{userEntity.communityId} AND tfsr.deleted = 0 ) > (SELECT count( tch.id ) FROM t_community_hardware tch WHERE tch.community_id = #{userEntity.communityId} AND tch.deleted = 0 ) ) WHEN TRUE THEN '失败' ELSE '成功' END AS distributionStatusStr FROM t_user tu LEFT JOIN t_house_member thm ON thm.uid = tu.uid WHERE tu.face_url IS NOT NULL AND thm.community_id = #{userEntity.communityId} AND tu.deleted = 0 AND thm.deleted = 0
AND (tu.real_name like concat('%', #{userEntity.keyword}, '%') OR tu.mobile like concat('%', #{userEntity.keyword}, '%'))
AND tu.face_enable_status = #{userEntity.faceEnableStatus}
AND CASE ( ( SELECT count( tfsr.id ) FROM t_user_face_sync_record tfsr WHERE tfsr.uid = tu.uid AND tfsr.face_url = tu.face_url AND tfsr.community_id = #{userEntity.communityId} AND tfsr.deleted = 0 ) > ( SELECT count( tch.id ) FROM t_community_hardware tch WHERE tch.community_id = #{userEntity.communityId} AND tch.deleted = 0 ) ) WHEN TRUE THEN 1 ELSE 2 END = 1
AND CASE ( ( SELECT count( tfsr.id ) FROM t_user_face_sync_record tfsr WHERE tfsr.uid = tu.uid AND tfsr.face_url = tu.face_url AND tfsr.deleted = 0 ) > ( SELECT count( tch.id ) FROM t_community_hardware tch WHERE tch.community_id = 1 AND tch.deleted = 0 ) ) WHEN TRUE THEN 1 ELSE 2 END = 2
AND thm.house_id = #{userEntity.houseId}
AND thm.relation = #{userEntity.relationCode}
group by tu.uid, thm.relation limit #{startNum}, #{endNum}

SELECT count(DISTINCT tu.uid) FROM t_user tu LEFT JOIN t_house_member thm ON thm.uid = tu.uid WHERE tu.face_url IS NOT NULL AND thm.community_id = #{userEntity.communityId} AND tu.deleted = 0 AND thm.deleted = 0
AND (tu.real_name like concat('%', #{userEntity.keyword}, '%') OR tu.mobile like concat('%', #{userEntity.keyword}, '%'))
AND tu.face_enable_status = #{userEntity.faceEnableStatus}
AND CASE ( ( SELECT count( tfsr.id ) FROM t_user_face_sync_record tfsr WHERE tfsr.uid = tu.uid AND tfsr.face_url = tu.face_url AND tfsr.community_id = #{userEntity.communityId} AND tfsr.deleted = 0 ) > ( SELECT count( tch.id ) FROM t_community_hardware tch WHERE tch.community_id = #{userEntity.communityId} AND tch.deleted = 0 ) ) WHEN TRUE THEN 1 ELSE 2 END = 1
AND CASE ( ( SELECT count( tfsr.id ) FROM t_user_face_sync_record tfsr WHERE tfsr.uid = tu.uid AND tfsr.face_url = tu.face_url AND tfsr.community_id = #{userEntity.communityId} AND tfsr.deleted = 0 ) > ( SELECT count( tch.id ) FROM t_community_hardware tch WHERE tch.community_id = #{userEntity.communityId} AND tch.deleted = 0 ) ) WHEN TRUE THEN 1 ELSE 2 END = 2
AND thm.house_id = #{userEntity.houseId}
AND thm.relation = #{userEntity.relationCode}
