mapper.select:
SELECT u.id uid,u.real_name urn,ci.id cid,ci.title cit,ti.inform_status isf
FROM t_user u join t_user_inform ti on u.id=ti.user_id join t_community_inform ci on ti.inform_id=ci.id
where ci.id=#{informId}

SELECT count(*)
FROM t_user u join t_user_inform ti on u.id=ti.user_id join t_community_inform ci on ti.inform_id=ci.id
where ci.id=#{informId}
